mapper.select:
  select itemid,itemname,price,description,pictureurl
 from (select rownum rnum,itemid,itemname,price,description,pictureurl from (select * FROM item ORDER BY itemid DESC))
where rnum > =#{start} and rnum <= #{end};

select itemid, itemname, price, description, pictureurl
from item
where itemid = #{itemid}
